INSERT INTO `craft_matrixcontent_projectdetails` (`id`, `elementId`, `locale`, `field_projectDetails_clientName`, `field_projectDetails_services`, `field_projectDetails_shortDescription`, `field_projectDetails_websiteUrl`, `dateCreated`, `dateUpdated`, `uid`) VALUES
 ('1', '108', 'en_us', 'IndieRabbit', '[\"characterDesign\",\"illustration\"]', '<p><em>IndieRabbit</em> was a small web startup that offered a unique distribution platform for independent filmmakers and their audience members. It was my task to design a mascot for the company as well as to produce several illustrations for their marketing materials and site.</p>', '', '2017-05-13 03:30:22', '2017-05-16 22:35:05', 'c2f4fb3d-a9a9-4854-812c-c32106cd1489'),
 ('2', '113', 'en_us', 'Everything Kitchens', '[\"frontEndDevelopment\",\"magento\",\"eCommerce\"]', '<p>This project is the largest Magento e-commerce build of my career so far. With an offering of over 200 different brands and thousands of products already in their inventory, the <em>Everything Kitchens</em> site took almost an entire year to complete simply because of its scale.</p>', 'https://www.everythingkitchens.com/', '2017-05-15 21:17:00', '2017-05-16 01:57:27', '20f93f99-472e-424f-bcd0-88f505544fc0'),
 ('3', '118', 'en_us', 'Benham Brothers', '[\"frontEndDevelopment\",\"wordpress\"]', '', 'http://benhambrothers.com/', '2017-05-15 21:53:36', '2017-05-16 13:56:06', '8c5bda62-935a-4211-95fc-e0f2649f2fa8'),
 ('4', '121', 'en_us', 'Senator Bob Hall', '[\"frontEndDevelopment\",\"wordpress\"]', '', 'http://senatorbobhall.com/', '2017-05-15 22:12:06', '2017-05-16 00:28:53', 'cf089da4-0b26-4fff-9c9e-2694c05828f9'),
 ('5', '124', 'en_us', 'Ohio Right to Life', '[\"frontEndDevelopment\"]', '', 'http://www.ohiolife.org/', '2017-05-15 22:14:22', '2017-05-16 00:28:43', '8f3271b1-ef65-4603-8a4c-804b5d189714'),
 ('6', '133', 'en_us', 'Revive Magazine', '[\"graphicDesign\",\"illustration\"]', '', '', '2017-05-16 21:32:54', '2017-05-16 21:32:54', '73fd540a-7325-4e1d-aebd-6dd1826070fb'),
 ('7', '136', 'en_us', 'Made to Flourish', '[\"branding\",\"graphicDesign\",\"illustration\"]', '', 'https://www.madetoflourish.org/', '2017-05-16 21:34:43', '2017-05-16 21:42:15', 'eac8f4f9-510f-4e81-90b6-e2c6a07be154');
INSERT INTO `craft_matrixcontent_testimonial` (`id`, `elementId`, `locale`, `field_testimonial_quote`, `field_testimonial_cite`, `field_testimonial_company`, `field_testimonial_role`, `dateCreated`, `dateUpdated`, `uid`) VALUES
 ('1', '8', 'en_us', '<p>“I’m a huge stickler for quality and pixel-perfect detail. Wilson delivers both on the daily. Not only did he design a killer rabbit (no Monty Python joke intended) mascot for our company, but he did so with a dedication to the spirit of our company unparalleled in the design world. He and his work are a treasure and I would without any doubt, recommend him for whatever project is on your mind.”</p>', 'Jean-Marc Le Doux', 'IndieRabbit', 'Co-founder', '2017-04-21 23:35:04', '2017-05-10 23:16:36', 'c1d72596-7f5e-490e-897d-fcbf24bf1623'),
 ('2', '10', 'en_us', '<p>“A master of detail, Wilson brings his all to every project, always looking for new ideas to take a concept deeper and deliver results above and beyond the required. In our case, he crafted a stunning book cover that seamlessly wove together our concepts and copy-heavy title into a visual masterpiece. A stellar designer, illustrator, and creative force, without hesitation I’ll tell you to book him today!”</p>', 'Jean-Marc Le Doux', 'Kern Family Foundation', 'Project Lead', '2017-04-21 23:36:45', '2017-05-10 23:31:11', '2981fb55-cb94-4a2a-b3ed-3366b8d3cb02'),
 ('3', '12', 'en_us', '<p>“Wilson has always been great to work with! His positive can-do attitude coupled with his creativity and rockstar skills has made him the perfect partner for a wide variety of projects including design, illustration, and web development.”</p>', 'Aaron Paulus', 'Flood Creative Agency', 'Founder', '2017-04-21 23:37:55', '2017-05-10 23:30:42', 'a314f5e1-6494-41b6-ab56-1fcf72fdc38b'),
 ('4', '22', 'en_us', '<p>“Wilson is a really sharp developer. It’s rare to have a developer who also has strong design skills, but Wilson possesses both of those skills which allows him to approach projects holistically. Over the years of working with him, his attention to detail resulted in many successful projects.”</p>', 'Erik Hansen', 'Classy Llama', 'Co-founder', '2017-05-10 23:16:03', '2017-05-10 23:30:06', 'a87d44e7-ed38-4d92-abaa-4652ff82e88e'),
 ('5', '24', 'en_us', '<p>“I’ve enjoyed working with Wilson for custom illustration because he owns the project and has always delivered above and beyond my expectations. He pays attention to detail and also to the big picture of what the art is meant to communicate.”</p>', 'Liza Hartman', 'Life Action Ministries', 'Art Director', '2017-05-10 23:18:22', '2017-05-10 23:29:14', '3eb0a900-936b-4b8b-a76e-452e587e2f86');
